query ArticleTemplate(
  $slug: String!
) {
  strapiArticles(slug: {eq: $slug}) {
    id
    title
    content
    slug
    author {
      id
      username
    }
    tag {
      id
      name
    }
  }
  allStrapiArticles {
    edges {
      node {
        id
        slug
        title
        shortdesc
        date
        content
        author {
          id
          username
        }
        tag {
          id
          name
        }
      }
    }
  }
}

query UserTemplate(
  $id: String!
) {
  strapiUsers(id: {eq: $id}) {
    id
    username
    article {
      id
      title
      shortdesc
      slug
    }
  }
}

query PageTemplate(
  $slug: String!
) {
  strapiPages(slug: {eq: $slug}) {
    id
    title
    content
    slug
    subPages {
      id
      title
      content
      slug
    }
    parentPage {
      id
      title
      slug
    }
  }
  allStrapiPages {
    edges {
      node {
        id
        title
        slug
        parentPage {
          id
          title
          slug
        }
        subPages {
          id
          title
          slug
        }
      }
    }
  }
  site {
    siteMetadata {
      title
    }
  }
}

query IndexQuery {
  allStrapiArticles {
    edges {
      node {
        id
        title
        shortdesc
        content
        date
        slug
        author {
          id
          username
        }
        tag {
          id
          name
        }
      }
    }
  }
}

query NewsQuery {
  allStrapiArticles(limit: 2, sort: {fields: [date], order: ASC}) {
    edges {
      node {
        id
        title
        shortdesc
        date(formatString: "MMMM DD YYYY")
        slug
        author {
          id
          username
        }
      }
    }
  }
}

query PagesQuery {
  allStrapiPages {
    edges {
      node {
        id
        title
        slug
        subPages {
          id
          title
          content
          slug
        }
      }
    }
  }
}
